public class Test09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 2;
		
		a += b;
		System.out.println(a);
		a -= b;
		System.out.println(a);
		a *= b;
		System.out.println(a);
		a /= b;
		System.out.println(a);
		a %= b;
		System.out.println(a);
		
	}

}
